DECLARE @run_id   NVARCHAR(64)  = '@{pipeline().RunId}';
DECLARE @stage    NVARCHAR(32)  = 'stg_to_core';
DECLARE @dataset  NVARCHAR(128) = '@{pipeline().parameters.dataset}';
DECLARE @ingest   DATE          = '@{pipeline().parameters.run_date}';

INSERT INTO ops.run_log (run_id, stage, dataset, ingest_date, status, started_at_utc)
SELECT @run_id, @stage, @dataset, @ingest, 'STARTED', SYSUTCDATETIME();

-- CoreDQ_Gate: log outcomes to ops.dq_result and fail on hard errors

DECLARE @run_id NVARCHAR(64)  = '@{pipeline().RunId}';
DECLARE @stage  NVARCHAR(32)  = 'stg_to_core';
DECLARE @d      DATE          = '@{pipeline().parameters.run_date}';
DECLARE @now    DATETIME2(3)  = SYSUTCDATETIME();

-- Row counts
DECLARE @rows_stg   BIGINT = (SELECT COUNT(*) FROM stg.trip        WHERE ingest_date=@d);
DECLARE @rows_core  BIGINT = (SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date=@d);
DECLARE @rows_err   BIGINT = (SELECT COUNT(*) FROM err.trip        WHERE ingest_date=@d);

-- Core consistency checks
DECLARE @bad_time BIGINT =
(SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date=@d AND pickup_ts_utc > dropoff_ts_utc);

DECLARE @neg_fare BIGINT =
(SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date=@d AND fare_amount < 0);

DECLARE @neg_dist BIGINT =
(SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date=@d AND trip_distance_km < 0);

DECLARE @null_pickup BIGINT =
(SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date=@d AND pickup_ts_utc IS NULL);

DECLARE @null_dropoff BIGINT =
(SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date=@d AND dropoff_ts_utc IS NULL);

-- Duplicates in core after dedupe (should be zero)
DECLARE @dupe_core BIGINT =
(
  SELECT COUNT(*) FROM (
    SELECT vendor_code, pickup_ts_utc, dropoff_ts_utc, fare_amount, trip_distance_km, payment_type
    FROM core.trip_clean
    WHERE ingest_date=@d
    GROUP BY vendor_code, pickup_ts_utc, dropoff_ts_utc, fare_amount, trip_distance_km, payment_type
    HAVING COUNT(*) > 1
  ) d
);

-- Core+Err should never exceed staging for a slice
DECLARE @core_plus_err_gt_stg BIT =
  CASE WHEN @rows_err IS NULL THEN 0
       WHEN (@rows_core + @rows_err) > @rows_stg THEN 1 ELSE 0 END;

-- Avoid duplicate log rows for retries/reruns of the same run_id
DELETE FROM ops.dq_result WHERE run_id=@run_id AND stage=@stage AND ingest_date=@d;

-- Log all outcomes (PASSED/FAILED)
INSERT INTO ops.dq_result
(run_id, stage, check_name, ingest_date, status, actual_value, threshold_value, error_message, created_at_utc)
SELECT @run_id, @stage, 'rowcount_core_gt_0', @d,
       CASE WHEN @rows_core > 0 THEN 'PASSED' ELSE 'FAILED' END,
       @rows_core, 1, NULL, @now
UNION ALL
SELECT @run_id, @stage, 'no_bad_time_order', @d,
       CASE WHEN @bad_time = 0 THEN 'PASSED' ELSE 'FAILED' END,
       @bad_time, 0, NULL, @now
UNION ALL
SELECT @run_id, @stage, 'no_negative_fare', @d,
       CASE WHEN @neg_fare = 0 THEN 'PASSED' ELSE 'FAILED' END,
       @neg_fare, 0, NULL, @now
UNION ALL
SELECT @run_id, @stage, 'no_negative_distance', @d,
       CASE WHEN @neg_dist = 0 THEN 'PASSED' ELSE 'FAILED' END,
       @neg_dist, 0, NULL, @now
UNION ALL
SELECT @run_id, @stage, 'no_null_pickup_ts', @d,
       CASE WHEN @null_pickup = 0 THEN 'PASSED' ELSE 'FAILED' END,
       @null_pickup, 0, NULL, @now
UNION ALL
SELECT @run_id, @stage, 'no_null_dropoff_ts', @d,
       CASE WHEN @null_dropoff = 0 THEN 'PASSED' ELSE 'FAILED' END,
       @null_dropoff, 0, NULL, @now
UNION ALL
SELECT @run_id, @stage, 'no_duplicate_core_rows', @d,
       CASE WHEN @dupe_core = 0 THEN 'PASSED' ELSE 'FAILED' END,
       @dupe_core, 0, NULL, @now
UNION ALL
SELECT @run_id, @stage, 'core_plus_err_le_stg', @d,
       CASE WHEN @core_plus_err_gt_stg = 0 THEN 'PASSED' ELSE 'FAILED' END,
       (@rows_core + @rows_err) - @rows_stg, 0, NULL, @now;

-- Hard fail if any FAILED status exists for this run/date
IF EXISTS (
  SELECT 1 FROM ops.dq_result
  WHERE run_id=@run_id AND stage=@stage AND ingest_date=@d AND status='FAILED'
)
  THROW 53000, 'DQ FAIL: One or more core checks failed. See ops.dq_result for details.', 1;

