DECLARE @run_id   NVARCHAR(64)  = '@{pipeline().RunId}';
DECLARE @stage    NVARCHAR(32)  = 'stg_to_core';
DECLARE @dataset  NVARCHAR(128) = '@{pipeline().parameters.dataset}';
DECLARE @ingest   DATE          = '@{pipeline().parameters.run_date}';

INSERT INTO ops.run_log (run_id, stage, dataset, ingest_date, status, started_at_utc)
SELECT @run_id, @stage, @dataset, @ingest, 'STARTED', SYSUTCDATETIME();

-- CoreDQ_Gate (core-layer data quality for a single ingest_date)
-- Assumes tables: stg.trip, core.trip_clean, err.trip (optional)
-- Fails the activity on any hard violation via THROW.

DECLARE @ingest_date DATE = '@{pipeline().parameters.run_date}';

-- Row counts
DECLARE @rows_stg   BIGINT = (SELECT COUNT(*) FROM stg.trip       WHERE ingest_date = @ingest_date);
DECLARE @rows_core  BIGINT = (SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date = @ingest_date);
DECLARE @rows_err   BIGINT = (SELECT COUNT(*) FROM err.trip       WHERE ingest_date = @ingest_date);

-- Core domain/consistency checks
DECLARE @bad_time         BIGINT = (
  SELECT COUNT(*) FROM core.trip_clean
  WHERE ingest_date=@ingest_date AND pickup_ts_utc > dropoff_ts_utc
);

DECLARE @neg_fare         BIGINT = (
  SELECT COUNT(*) FROM core.trip_clean
  WHERE ingest_date=@ingest_date AND fare_amount < 0
);

DECLARE @neg_distance     BIGINT = (
  SELECT COUNT(*) FROM core.trip_clean
  WHERE ingest_date=@ingest_date AND trip_distance_km < 0
);

DECLARE @null_pickup      BIGINT = (
  SELECT COUNT(*) FROM core.trip_clean
  WHERE ingest_date=@ingest_date AND pickup_ts_utc IS NULL
);

DECLARE @null_dropoff     BIGINT = (
  SELECT COUNT(*) FROM core.trip_clean
  WHERE ingest_date=@ingest_date AND dropoff_ts_utc IS NULL
);

DECLARE @dupe_trip_id     BIGINT = (
  SELECT COUNT(*)
  FROM (
    SELECT trip_id
    FROM core.trip_clean
    WHERE ingest_date=@ingest_date AND trip_id IS NOT NULL
    GROUP BY trip_id
    HAVING COUNT(*) > 1
  ) d
);

-- Optional sanity: core + err should not exceed staging (if you capture err rows)
DECLARE @core_plus_err_gt_stg BIT =
  CASE WHEN @rows_err IS NULL THEN 0
       WHEN (@rows_core + @rows_err) > @rows_stg THEN 1 ELSE 0 END;

-- Quick human-readable summary (shows in activity output logs)
SELECT
  @ingest_date  AS ingest_date,
  @rows_stg     AS rows_stg,
  @rows_core    AS rows_core,
  @rows_err     AS rows_err,
  @bad_time     AS bad_time_order_cnt,
  @neg_fare     AS negative_fare_cnt,
  @neg_distance AS negative_distance_cnt,
  @null_pickup  AS null_pickup_cnt,
  @null_dropoff AS null_dropoff_cnt,
  @dupe_trip_id AS duplicate_trip_id_cnt;

-- Hard fail conditions (stop the pipeline with a clear message)
IF @rows_core = 0
  THROW 52000, 'DQ FAIL: core.trip_clean has 0 rows for ingest_date.', 1;

IF @bad_time > 0
  THROW 52001, 'DQ FAIL: pickup_ts_utc > dropoff_ts_utc detected in core.', 1;

IF @neg_fare > 0
  THROW 52002, 'DQ FAIL: Negative fare_amount rows present in core.', 1;

IF @neg_distance > 0
  THROW 52003, 'DQ FAIL: Negative trip_distance_km rows present in core.', 1;

IF @null_pickup > 0
  THROW 52004, 'DQ FAIL: NULL pickup_ts_utc rows present in core.', 1;

IF @null_dropoff > 0
  THROW 52005, 'DQ FAIL: NULL dropoff_ts_utc rows present in core.', 1;

IF @dupe_trip_id > 0
  THROW 52006, 'DQ FAIL: Duplicate trip_id detected in core for this slice.', 1;

IF @core_plus_err_gt_stg = 1
  THROW 52007, 'DQ FAIL: (core + err) row count exceeds staging row count for this slice.', 1;

-- If we reach here, all checks passed (no THROW)

-- LogSuccess (after CoreDQ_Gate succeeds)
DECLARE @run_id   NVARCHAR(64)  = '@{pipeline().RunId}';
DECLARE @stage    NVARCHAR(32)  = 'stg_to_core';
DECLARE @dataset  NVARCHAR(128) = '@{pipeline().parameters.dataset}';
DECLARE @d        DATE          = '@{pipeline().parameters.run_date}';

DECLARE @rows BIGINT = (SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date = @d);

-- Try to update existing STARTED row
UPDATE ops.run_log
SET status = 'SUCCEEDED',
    rows_copied = @rows,
    finished_at_utc = CAST(SYSUTCDATETIME() AS DATETIME2(3))
WHERE run_id = @run_id
  AND stage  = @stage;

-- Fallback if no row exists (avoid @@ROWCOUNT)
IF NOT EXISTS (SELECT 1 FROM ops.run_log WHERE run_id = @run_id AND stage = @stage)
BEGIN
  INSERT INTO ops.run_log
    (run_id, stage, dataset, ingest_date, status, started_at_utc, finished_at_utc, rows_copied)
  SELECT
    @run_id, @stage, @dataset, @d, 'SUCCEEDED',
    CAST(SYSUTCDATETIME() AS DATETIME2(3)),
    CAST(SYSUTCDATETIME() AS DATETIME2(3)),
    @rows;
END

-- LogFailure (after CoreDQ_Gate fails)
DECLARE @run_id   NVARCHAR(64)  = '@{pipeline().RunId}';
DECLARE @stage    NVARCHAR(32)  = 'stg_to_core';
DECLARE @dataset  NVARCHAR(128) = '@{pipeline().parameters.dataset}';
DECLARE @d        DATE          = '@{pipeline().parameters.run_date}';

DECLARE @rows BIGINT = (SELECT COUNT(*) FROM core.trip_clean WHERE ingest_date = @d);

-- Try to update existing STARTED row
UPDATE ops.run_log
SET status = 'FAILED',
    rows_copied = @rows,
    finished_at_utc = CAST(SYSUTCDATETIME() AS DATETIME2(3))
WHERE run_id = @run_id
  AND stage  = @stage;

-- Fallback if no row exists (avoid @@ROWCOUNT)
IF NOT EXISTS (SELECT 1 FROM ops.run_log WHERE run_id = @run_id AND stage = @stage)
BEGIN
  INSERT INTO ops.run_log
    (run_id, stage, dataset, ingest_date, status, started_at_utc, finished_at_utc, rows_copied)
  SELECT
    @run_id, @stage, @dataset, @d, 'FAILED',
    CAST(SYSUTCDATETIME() AS DATETIME2(3)),
    CAST(SYSUTCDATETIME() AS DATETIME2(3)),
    @rows;
END

DECLARE @run_id NVARCHAR(64)='@{pipeline().RunId}';
DECLARE @stage  NVARCHAR(32)='raw_to_stg';
DECLARE @d      DATE='@{pipeline().parameters.run_date}';
DECLARE @now    DATETIME2(3)=SYSUTCDATETIME();
DECLARE @cnt    BIGINT;
DECLARE @status NVARCHAR(16);

SELECT @cnt = COUNT(*) FROM stg.trip WHERE ingest_date=@d;
SET @status = CASE WHEN @cnt>0 THEN 'PASSED' ELSE 'FAILED' END;

INSERT INTO ops.dq_result (run_id,stage,check_name,ingest_date,status,actual_value,threshold_value,error_message,created_at_utc)
SELECT @run_id,@stage,'rowcount_gt_0',@d,@status,@cnt,1,NULL,@now;

IF @cnt = 0
  THROW 51000, 'DQ Rowcount failed: 0 rows loaded for ingest_date', 1;

DECLARE @run_id NVARCHAR(64)='@{pipeline().RunId}';
DECLARE @stage  NVARCHAR(32)='raw_to_stg';
DECLARE @d      DATE='@{pipeline().parameters.run_date}';
DECLARE @now    DATETIME2(3)=SYSUTCDATETIME();
DECLARE @bad    BIGINT;
DECLARE @status NVARCHAR(16);

SELECT @bad = COUNT(*) FROM stg.trip WHERE ingest_date=@d AND tpep_pickup_datetime IS NULL;
SET @status = CASE WHEN @bad=0 THEN 'PASSED' ELSE 'FAILED' END;

INSERT INTO ops.dq_result (run_id,stage,check_name,ingest_date,status,actual_value,threshold_value,error_message,created_at_utc)
SELECT @run_id,@stage,'no_null_pickup_ts',@d,@status,@bad,0,NULL,@now;

IF @bad > 0
  THROW 51001, 'DQ Null pickup datetime failed', 1;

DECLARE @run_id NVARCHAR(64)='@{pipeline().RunId}';
DECLARE @stage  NVARCHAR(32)='raw_to_stg';
DECLARE @d      DATE='@{pipeline().parameters.run_date}';
DECLARE @now    DATETIME2(3)=SYSUTCDATETIME();
DECLARE @neg    BIGINT;
DECLARE @status NVARCHAR(16);

SELECT @neg = COUNT(*) FROM stg.trip WHERE ingest_date=@d AND fare_amount < 0;
SET @status = CASE WHEN @neg=0 THEN 'PASSED' ELSE 'WARNING' END;

INSERT INTO ops.dq_result (run_id,stage,check_name,ingest_date,status,actual_value,threshold_value,error_message,created_at_utc)
SELECT @run_id,@stage,'no_negative_fare',@d,@status,@neg,0,NULL,@now;

/* No THROW here – we only log and proceed */

DECLARE @run_id     NVARCHAR(64)  = '@{pipeline().RunId}';
DECLARE @stage      NVARCHAR(32)  = 'raw_to_stg';
DECLARE @dataset    NVARCHAR(128) = '@{pipeline().parameters.dataset}';
--DECLARE @ingest     DATE          = '@{pipeline().parameters.run_date}';

DECLARE @ingest_date_str NVARCHAR(32) = '@{pipeline().parameters.run_date}';
DECLARE @ingest DATE = TRY_CONVERT(DATE, @ingest_date_str, 23);
IF @ingest IS NULL
  THROW 53001, 'Bad run_date format. Expected yyyy-MM-dd.', 1;

DECLARE @started    DATETIME2(3)  = SYSUTCDATETIME();

INSERT INTO ops.run_log(run_id, stage, dataset, ingest_date, status, started_at_utc)
VALUES (@run_id, @stage, @dataset, @ingest, 'STARTED', @started);

