IF NOT EXISTS (SELECT 1 FROM sys.database_principals WHERE name='role_ingest_loader')
    CREATE ROLE role_ingest_loader;

-- Allow loaders to write/read staging
GRANT SELECT, INSERT, UPDATE, DELETE ON SCHEMA::stg TO role_ingest_loader;
